import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom'



class TodoApp extends Component {
    render() {
        return (
            <div className="App">
                <Router>
                    <>
                    <HeaderComponent/>
                    <Switch>
                    <Route path="/" exact component={LoginComponent}/>
                    <Route path="/login" component={LoginComponent}/>
                    <Route path="/welcome/:name" component={WelcomeComponent}/>
                    <Route path="/todos" component={ListTodosComponent}/>
                    <Route path="/logout" component={LogoutComponent}/>
                    <Route component={ErrorComponent}/>
                    </Switch>
                    <FooterComponent/>
                    </>
                </Router>
                {/* <LoginComponent/>
                <WelcomeComponent/> */}
            </div>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        return (
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div><a href="/welcome/robby" className="navbar-brand">Nimbus</a></div>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav ml-auto">
                    <li className="nav-item">
                        <Link className="nav-link" to="/welcome/robby">Home</Link>
                        </li>
                    <li className="nav-item">
                        <Link className="nav-link" to="/todos">Todos</Link>
                        </li>
                    <li class="nav-item">
                        <Link className="nav-link" to="/login">Login</Link>
                    </li>
                    <li class="nav-item">
                        <Link className="nav-link" to="/logout">Logout</Link>
                        </li>
                </ul>
                </div>
            </nav>
        )
    }
}

class FooterComponent extends Component {
    render() {
        return (
        <footer className="footer">
            <span className="text-muted">Copright 2020 Nimbus</span>
        </footer>
        )
    }
}

class LogoutComponent extends Component {
    render() {
        return (
            <>
                <h1>You are logged out</h1>
                <div className="containter">
                    Thank You for Using Nimbus!
                </div>
            </>
        )
    }
}

class ListTodosComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            todos : 
            [
            {id: 1, description : 'Learn React', done:false, targetDate: new Date()},
            {id: 2, description : 'Learn React', done:false, targetDate: new Date()},
            {id: 3, description : 'Learn React', done:false, targetDate: new Date()},
            ]
        }
    }
    render() {
        return (
            <div>
            <h1>List Todos</h1>
            <div class="container">
            <table class="table">
                <thead>
                    <tr>
                        <th>id</th>
                        <th>description</th>
                        <th>Target Date</th>
                        <th>Is Completed?</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        this.state.todos.map(
                            todo =>
                            <tr>
                                <td>{todo.id}</td>
                                <td>{todo.description}</td>
                                <td>{todo.done.toString()}</td>
                                <td>{todo.targetDate.toString()}</td>
                            </tr>
                        )
                    }
                </tbody>
            </table>
            </div>
            </div>
        )
    }
}

class WelcomeComponent extends Component {
    render() {
        return (
            <>
            <h1>Welcome!</h1>
            <div class="container">
            Welcome {this.props.match.params.name}. You can manage your todos <Link to="/todos">here.</Link>
            </div>
            </>
       )
    }

}

function ErrorComponent() {
    return <div>An Error Occured. Contact Support</div>
}

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: 'robby',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false,
        }
        // this.handleUsernameChange = this.handleUsernameChange.bind(this)
        // this.handlePasswordChange = this.handlePasswordChange.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }



    handleChange(event) {
        console.log(event.target.name);
        this.setState (
            {
                [event.target.name]
                    :event.target.value
                }
            )
    }

    // handleUsernameChange(event) {
    //     console.log(event.target.value);
    //     this.setState (
    //         {
    //             [event.target.name]
    //                 :event.target.value
    //             }
    //         )
    // }

    // handlePasswordChange(event) {
    //     console.log(event.target.value);
    //     this.setState ({password:event.target.value})

    // }

    loginClicked() {
        if(this.state.username==='robby' && this.state.password==='dummy') {
            this.props.history.push(`/welcome/${this.state.username}`)
            // this.setState({showSuccessMessage:true})
            // this.setState({hasLoginFailed:false})
        }
        else {
            this.setState({showSuccessMessage:false})
            this.setState({hasLoginFailed:true})

        }

        // console.log(this.state)
    }

    render() {
        return (
            <div>
                <h1>Login</h1>
                <div className="container">
                {/* <ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}/> */}
                {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                {this.state.showSuccessMessage && <div>Login Successful</div>}
                {/* <ShowLoginSuccessMessage showSuccessMessage={this.state.showSuccessMessage}/> */}
            User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
            Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
            <button className="btn btn=" onClick={this.loginClicked}>Login</button>
            </div>
            </div>
        )
    }
}

// function ShowInvalidCredentials(props) {
//     if(props.hasLoginFailed) {
//         return <div>Invalid Credentials</div>
//     }
//     return null

// }

// function ShowLoginSuccessMessage(props) {
//     if(props.showSuccessMessage) {
//         return <div>Login Successful</div>
//     }
//     return null
// }

export default TodoApp
